            <div class="row">
                <div class="col-md-6">
                    <h6 class="mb-1">
                        My Referral Link :
                    </h6>
                    <p>
                        {{ route('frontend.register') . '?ref=' . auth('representative')->user()->code }}
                        <button class="ms-2"
                            onclick="copyToClipboard('{{ route('frontend.register') . '?ref=' . auth('representative')->user()->code }}')">
                            <i class="fa fa-copy"></i>
                        </button>
                        <script>
                            function copyToClipboard(text) {
                                navigator.clipboard.writeText(text).then(function() {
                                    toastr.success('Link Copied Successfully');
                                }, function(err) {
                                    console.error('Could not copy text: ', err);
                                });
                            }
                        </script>
                    </p>
                </div>

                <div class="col-md-6">
                    {{-- preview the referrlaed dealers count --}}
                    <h6 class="mb-1">
                        My Referred Dealers :
                    </h6>
                    <p>
                        {{ $referredDealersCount }}
                    </p>
                </div>
