        </div>
        <div class="row">
            @forelse ($ordersCountByStatus as $status => $count)
                <div class="col-md-6 pt-30">
                    <div class="text-center single-content">
                        <h6 style="text-transform: capitalize"> {{ $status }} Orders</h6>
                        <h2>{{ $count }}</h2>
                    </div>
                </div>
            @empty
                <div class="col-md-12 pt-30">
